--VALUES ('OCULTAR_REGRAS_CONCESSAO_HORARIO_ESPECIAL','Define se, ao cadastrar o horário especial por meio do Portal do Servidor ou da chefia de unidade, os campos da seção "Regras da Concessão" devem estar ocultos ou não, já que estes vêm sempre pré-carregados com seus valores padrões, nesses casos.','false',101600,7,'7_101600_18',NULL,16,NULL,NULL,NULL);

