--VALUES ('PERMITIR_CHEFIA_AUTORIZAR_HORARIO_ESTUDANTE','Se estiver com o valor "true", permite que, quando um chefe homologar uma solicitação de concessão de horário especial de estudante feita por seus servidores, essa concessão já seja automaticamente autorizada, sem precisar passar pelo RH.','false',101600,7,'7_101600_19',0,16,NULL,NULL,NULL);
